import React, {useState} from 'react';
import { Button, TextField, Switch , FormControlLabel , Slider , Chip , Stack} from '@mui/material';
import './FormularioCadastro.css';

function FormularioCadastro({aoEnviar, validarCPF}) {
    const [nome,setNome] = useState("");
    const [sobrenome,setSobrenome] =  useState("");
    const [cpf,setCpf] = useState("");
    const [promocoes,setPromocoes] =  useState(true);
    const [novidades,setNovidades] = useState(true);
    const [g_esf_esq, setGrauEsfEsq] = useState(0);
    const [g_esf_dir, setGrauEsfDir] = useState(0);

    const [erros, setErros] = useState({cpf:{valido:true, texto:""}});
    return (
        <form onSubmit={(event) => {
            event.preventDefault();
            aoEnviar({nome, sobrenome, cpf, promocoes, novidades});   
        }}>

            <Slider 
                value={g_esf_esq}
                onChange={event => {
                    setGrauEsfEsq(event.target.value);
                }}
                step={0.25} 
                marks={true}
                min={-15} 
                max={0} 
            />

            <Slider 
                value={g_esf_dir}
                onChange={event => {
                    setGrauEsfDir(event.target.value);
                }}
                step={0.25} 
                marks={true}
                min={-15} 
                max={0} 
            />

            
            <Stack direction="row" spacing={1}>

                <Chip label={"Grau Esférico do olho Esquerdo: " + g_esf_esq} color="primary" />
                <Chip label={"Grau Esférico do olho Direito: " + g_esf_dir} color="success" />

            </Stack>
            <Stack direction="row" spacing={1}>
                <Chip label="primary" color="primary" variant="outlined" />
                <Chip label="success" color="success" variant="outlined" />
            </Stack>    

            <TextField
                value={nome}
                onChange={event => {
                    setNome(event.target.value);
                }}
                id="nome"
                label="Nome"
                variant="outlined"
                margin="normal"
                fullWidth
            />

            <TextField
                value={sobrenome}
                onChange={(event) => {
                    setSobrenome(event.target.value);
                }}
                id="sobrenome"
                label="Sobrenome"
                variant="outlined"
                margin="normal"
                fullWidth
            />

            <TextField
                value={cpf}
                onChange={(event) => {
                    setCpf(event.target.value);
                }}
                onBlur={(event) => {
                    const ehValido = validarCPF(cpf);
                    setErros({cpf:ehValido});
                }}
                error={!erros.cpf.valido}
                helperText={erros.cpf.texto}
                id="CPF"
                label="CPF"
                variant="outlined"
                margin="normal"
                fullWidth
            />

            <Button variant="contained" type="submit">
                Verificar lentes
            </Button>
        </form>
    );
}

export default FormularioCadastro;
