import './App.css';
import FormularioCadastro from './components/FormularioCadastro/FormularioCadastro';
import Resultados from './components/Resultados/Resultados';
import React, {useState} from 'react';

import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';

import { Container, Typography } from '@mui/material';

function App() {
  const [resultado, setResultado] = useState('');
  return (
    <Container component='article' maxWidth='sm'>
      <Typography variant='h3' align='center' component='h1'>Formulário de Cadastro</Typography>
      <FormularioCadastro aoEnviar={AoEnviarForm}/>
      <Resultados resultado={resultado}/>
    </Container>
  );
}

function AoEnviarForm(dados){
  const [lenteEsquerda, setLenteEsquerda] = useState('');
  if (dados.g_cil_esq == 0 && dados.g_esf_esq <= -3 && dados.g_esf_esq >= -12) {
    setLenteEsquerda("Prime");
  }
  else if (dados.g_cil_esq >= -2 && dados.g_esf_esq <= -3 && dados.g_esf_esq >= -10) {
    setLenteEsquerda("Prime");
  }
  else if (dados.g_cil_esq >= -5 && dados.g_esf_esq >= -15) {
    setLenteEsquerda("Vision");
  }
  else {
    console.log("Não há lente disponível para esses parâmetros, entre em contato com nossos consultores.");
  }

  
}

function validarCPF(cpf) {
  if(cpf.length !== 11) {
    return {valido:false, texto:"O CPF deve ter 11 dígitos."}
  }
  else {
    return {valido:true, texto:""}
  }
}

export default App;
